mapper.select:
select count(*) from tbl_clue


        appellation like '%' #{appellation} '%'


        and source=#{source}


        and state=#{state}


        and owner like '%' #{owner} '%'


        and phone=#{phone}


        and mphone=#{mphone}


        and company like '%' #{company} '%'

select c.id, c.fullname, c.appellation, c.company, c.job, c.email, c.phone, c.website, c.mphone, c.state, c.source, c.createBy, c.createTime, c.description, c.contactSummary, c.nextContactTime, c.address, u.name owner from tbl_clue c left join tbl_user u on c.owner=u.id
c.appellation like '%' #{appellation} '%'
and c.source=#{source}
and c.state=#{state}
and u.name like '%' #{owner} '%'
and c.phone=#{phone}
and c.mphone=#{mphone}
and c.company like '%' #{company} '%'
