mapper.select:
select count(*) from tbl_tran tr left join tbl_user u on tr.owner=u.id left join tbl_customer cust on tr.customerId=cust.id left join tbl_contacts con on tr.contactsId=con.id
tr.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and con.fullname like '%' #{contactsId} '%'
and cust.name like '%' #{customerId} '%'
and tr.type=#{type}
and tr.source=#{source}
and tr.stage=#{stage}

select tr.id, u.name owner, tr.money, tr.name, tr.expectedDate, cust.name customerId, tr.stage, tr.type, tr.source, tr.activityId, con.fullname contactsId, tr.createBy, tr.createTime, tr.description, tr.contactSummary, tr.nextContactTime from tbl_tran tr left join tbl_user u on tr.owner=u.id left join tbl_customer cust on tr.customerId=cust.id left join tbl_contacts con on tr.contactsId=con.id
tr.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and con.fullname like '%' #{contactsId} '%'
and cust.name like '%' #{customerId} '%'
and tr.type=#{type}
and tr.source=#{source}
and tr.stage=#{stage}

mapper.insert:
insert into tbl_tran(
id, owner, money, name, expectedDate, customerId, stage, type, source, activityId, contactsId, createBy, createTime, description, contactSummary, nextContactTime
)
values(
#{id}, #{owner}, #{money}, #{name}, #{expectedDate}, #{customerId}, #{stage}, #{type}, #{source}, #{activityId}, #{contactsId}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime}
)
